import React, { useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import { motion } from 'framer-motion';
import AOS from 'aos'; 
import 'aos/dist/aos.css'; 

// Keyframes untuk animasi bergerak ke kanan dan ke kiri
const moveRightLeft = keyframes`
  0%, 100% {
    transform: translateX(-100%);
  }
  50% {
    transform: translateX(100%);
  }
`;

// Keyframes untuk efek blur dan noise gradient
const gradientNoise = keyframes`
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
`;

// Hero section dengan warna gradient lebih gelap (abu-abu ke hitam)
const HeroSection = styled.section`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background: linear-gradient(150deg, #007bff, #ffffff, #333333, #007bff); /* Ditambahkan warna lebih gelap */
  background-size: 200% 200%;
  animation: ${gradientNoise} 50s ease infinite;
  color: gradient(120deg, #ffffff, #000);
  text-align: center;
  padding-top: 3rem;
  position: relative;
  overflow: hidden;
`;

// Hero title dengan posisi teks utama
const HeroTitle = styled(motion.h1)`
  font-size: 5rem;
  font-family: 'Inter', sans-serif;
  font-weight: 1000;
  margin-bottom: 0rem;
  position: relative;
  z-index: 3; /* Pastikan di atas blur dan text running */
`;

// Subtitle dengan efek blur di bawah teks
const HeroSubtitle = styled(motion.p)`
  font-size: 2rem;
  color: #555;
  z-index: 3;
`;

// Elemen untuk membuat efek blur di atas text running
const BlurEffect = styled.div`
  position: absolute;
  top: 50%;
  left: 0;
  width: 100%;
  height: 50%;
  background: rgba(255, 255, 255, 0.3); /* Efek blur ringan */
  filter: blur(60px); /* Efek blur */
  z-index: 2; /* Efek blur berada di atas text running */
`;

// Text running dengan efek blur dan berada di tengah
const RunningText = styled.div`
  position: absolute;
  top: 40%; /* Menempatkan teks di tengah secara vertikal */
  left: 0;
  width: 180%;
  font-size: 8rem;
  font-family: 'Inter', sans-serif;
  font-weight: 900; /* Maximum boldness */
  color: #3333; /* Warna hitam solid untuk kejelasan maksimal */
  white-space: nowrap;
  text-align: center; /* Menempatkan teks di tengah secara horizontal */
  filter: blur(8px); /* Menambahkan efek blur pada text running */
  animation: ${moveRightLeft} 20s infinite linear;
  z-index: 1; /* Text running berada di bawah blur */
`;


const Hero = () => {
  useEffect(() => {
    AOS.init({
      duration: 1000,
      once: false,
    });
  }, []);

  return (
    <HeroSection>
      <HeroTitle
        data-aos="fade-up"
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        Welcome To My Portfolio
      </HeroTitle>

      <HeroSubtitle
        data-aos="fade-up"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5, duration: 1 }}
      >
        Graphic Designer | UI/UX Designer | Programmer
      </HeroSubtitle>

      {/* Elemen untuk efek blur di atas text running */}
      <BlurEffect />

      {/* Text running dengan blur dan berada di tengah */}
      <RunningText>
        PORTOFOLIO RINO RAIHAN G
      </RunningText>
    </HeroSection>
  );
};

export default Hero;
