import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { containerVariants, itemVariants } from './Variants';

// Styled component for About Section
const AboutSection = styled(motion.section)`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  padding: 5rem 2rem;
  background-color: #e6f7ff;
  color: #333;
  text-align: left;
  min-height: 100vh;
  gap: 2rem; /* Space between image and content */
`;

// Styled component for Image
const ImageContainer = styled(motion.div)`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ProfileImage = styled.img`
  border-radius: 50%;
  width: 250px;
  height: 250px;
  object-fit: cover;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
`;

// Styled component for Content
const ContentContainer = styled(motion.div)`
  flex: 2;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
`;

const AboutTitle = styled(motion.h2)`
  font-size: 3rem;
  font-weight: bold;
  margin-bottom: 1.5rem;
`;

const AboutContent = styled(motion.p)`
  font-size: 1.5rem;
  line-height: 1.8;
  color: #555;
  max-width: 800px;
`;

// About Component
const About = () => {
  return (
    <AboutSection
      id="about"
      variants={containerVariants}
      initial="hidden"
      whileInView="visible"
      transition={{ duration: 0.8 }}
      viewport={{ once: true }}
    >
      <ImageContainer
        variants={itemVariants}
        transition={{ duration: 1 }}
      >
        <ProfileImage src="path_to_your_image.jpg" alt="Your Name" />
      </ImageContainer>
      <ContentContainer>
        <AboutTitle
          variants={itemVariants}
          transition={{ duration: 1 }}
        >
          Tentang Saya
        </AboutTitle>
        <AboutContent
          variants={itemVariants}
          transition={{ delay: 0.5, duration: 1 }}
        >
          Saya adalah seorang desainer grafis dengan pengalaman lebih dari 5 tahun dalam menciptakan desain yang menarik dan fungsional untuk berbagai merek. Saya berfokus pada kreativitas, inovasi, dan detail dalam setiap proyek yang saya kerjakan.
        </AboutContent>
      </ContentContainer>
    </AboutSection>
  );
};

export default About;
