// src/components/Header.js
import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';

// Styled component untuk header
const HeaderContainer = styled(motion.header)`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem 2rem;
  width: 100vw; /* Sesuaikan lebar header dengan viewport */
  max-width: 1700px; /* Membatasi lebar maksimal seperti hero section */
  margin: 0 auto; /* Agar header berada di tengah */
  background-color: #fff;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.05);
  position: fixed;
  top: 0;
  z-index: 1000;
  transition: background-color 0.4s ease, box-shadow 0.4s ease;
  
  &:hover {
    background-color: rgba(255, 255, 255, 0.9);
    box-shadow: 0 6px 12px rgba(0, 0, 0, 0.1);
  }
`;

// Logo / Branding di Navbar
const Logo = styled(Link)`
  font-size: 1.8rem;
  font-weight: bold;
  color: #007bff;
  text-decoration: none;
  font-family: 'Montserrat', sans-serif;
  transition: color 0.3s ease;

  &:hover {
    color: #0056b3;
  }
`;

// Styled component untuk navigasi
const Nav = styled.nav`
  display: flex;
  align-items: center;
  gap: 2.5rem;
`;

// Styled component untuk link navigasi
const NavLink = styled(Link)`
  font-size: 1.2rem;
  font-weight: 500;
  color: #333;
  position: relative;
  text-transform: uppercase;
  letter-spacing: 1.5px;
  text-decoration: none;
  transition: color 0.3s ease, transform 0.3s ease;

  &:hover {
    color: #007bff;
    transform: translateY(-3px);
    
    &::after {
      content: '';
      position: absolute;
      left: 0;
      bottom: -6px;
      width: 100%;
      height: 2px;
      background: #007bff;
      transform: scaleX(1);
      transition: transform 0.3s ease;
    }
  }

  &::after {
    content: '';
    position: absolute;
    left: 0;
    bottom: -6px;
    width: 100%;
    height: 2px;
    background: transparent;
    transform: scaleX(0);
    transition: transform 0.3s ease;
  }
`;

// Styled component untuk tombol CTA (Call To Action)
const CTAButton = styled(Link)`
  padding: 0.6rem 1.2rem;
  background-color: #007bff;
  color: #fff;
  border-radius: 30px;
  font-weight: bold;
  text-transform: uppercase;
  text-decoration: none;
  letter-spacing: 1px;
  transition: background-color 0.3s ease, transform 0.3s ease;

  &:hover {
    background-color: #0056b3;
    transform: scale(1.05);
  }
`;

// Komponen Header
const Header = () => {
  return (
    <HeaderContainer
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      transition={{ type: 'spring', stiffness: 50 }}
    >
      <Logo to="/">GraphicDesigner</Logo>
      <Nav>
        <NavLink to="/">Home</NavLink>
        <NavLink to="/projects">Projects</NavLink>
        <NavLink to="/about">About</NavLink>
        <NavLink to="/contact">Contact</NavLink>
        <CTAButton to="/contact">Hire Me</CTAButton>
      </Nav>
    </HeaderContainer>
  );
};

export default Header;
