// src/components/About.js
import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { containerVariants, itemVariants } from './Variants';
import myPhoto from '../assets/gambar/rino.JPG'; // Import your photo

// Styled component untuk About Section
const AboutSection = styled(motion.section)`
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 5rem 2rem;
  color: #333;
  min-height: 100vh; /* Mengatur tinggi minimal seperti hero */
`;

// Styled component untuk kontainer yang mengatur layout
const AboutContainer = styled.div`
  display: flex;
  align-items: center;
  width: 100%;
  max-width: 1200px; /* Maksimal lebar untuk menjaga layout tetap rapi */
  margin: 0 auto;
`;

// Styled component untuk gambar
const AboutImage = styled(motion.img)`
  width: 40%; /* Lebar gambar */
  border-radius: 8px; /* Rounded corners */
  margin-right: 2rem; /* Jarak antara gambar dan teks */
`;

// Styled component untuk konten deskripsi
const AboutContent = styled.div`
  flex: 1; /* Agar konten mengisi sisa ruang */
  max-width: 600px; /* Maksimal lebar konten untuk menjaga layout tetap rapi */
`;

// Styled component untuk judul di About Section
const AboutTitle = styled(motion.h2)`
  font-size: 3rem;
  font-weight: bold;
  margin-bottom: 1rem;
`;

// Styled component untuk konten paragraf di About Section
const AboutDescription = styled(motion.p)`
  font-size: 1.5rem;
  line-height: 1.8;
  color: #555;
`;

// Komponen About
const About = () => {
  return (
    <AboutSection
      id="about" // Tambahkan ID agar bisa digulir ke sini dari navbar
      variants={containerVariants}
      initial="hidden"
      whileInView="visible"
      transition={{ duration: 0.8 }}
      viewport={{ once: true }}
    >
      <AboutContainer>
        <AboutImage
          src= "./assets/gambar/rino.JPG" 
          alt="Rino"
          variants={itemVariants}
          transition={{ duration: 1 }}
        />
        <AboutContent>
          <AboutTitle
            variants={itemVariants}
            transition={{ duration: 1 }}
          >
            Tentang Saya
          </AboutTitle>
          <AboutDescription
            variants={itemVariants}
            transition={{ delay: 0.5, duration: 1 }}
          >
            Saya adalah seorang desainer grafis dengan pengalaman lebih dari 5 tahun dalam menciptakan desain yang menarik dan fungsional untuk berbagai merek.
          </AboutDescription>
        </AboutContent>
      </AboutContainer>
    </AboutSection>
  );
};

export default About;
