// src/pages/Projects.js
import React, { useEffect } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import AOS from 'aos'; // Import AOS
import 'aos/dist/aos.css'; // Import AOS styles

const PortfolioGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 20px;
  padding: 4rem 2rem;
  background-color: #fff;
`;

const ProjectCard = styled(motion.div)`
  background-color: #fff;
  padding: 1.5rem;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  border-radius: 8px;
  transition: transform 0.3s ease;
  &:hover {
    transform: scale(1.05);
  }
`;

const Projects = () => {
  useEffect(() => {
    AOS.init({
      duration: 1000,
      once: false,
    });
  }, []);

  const projects = [
    { title: "Project 1", description: "Brand identity for X company", image: "path_to_image1.jpg" },
    { title: "Project 2", description: "Website design for Y company", image: "path_to_image2.jpg" },
    // Tambahkan lebih banyak proyek di sini
  ];

  return (
    <PortfolioGrid>
      {projects.map((project, index) => (
        <ProjectCard
          key={index}
          data-aos="fade-up"
        >
          <img src={project.image} alt={project.title} style={{ width: '100%', borderRadius: '8px' }} />
          <h3>{project.title}</h3>
          <p>{project.description}</p>
        </ProjectCard>
      ))}
    </PortfolioGrid>
  );
};

export default projects;
