// src/components/Experience.js
import React, { useEffect } from 'react'; // Add useEffect here
import styled from 'styled-components';
import { motion } from 'framer-motion';
import AOS from 'aos';
import 'aos/dist/aos.css';

// Styled component untuk Experience Section
const ExperienceSection = styled(motion.section)`
  padding: 5rem 2rem;
  color: #333;
  background-color: #f9f9f9; /* Background color for contrast */
`;

// Styled component untuk kontainer pengalaman
const ExperienceContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
`;

// Styled component untuk judul di Experience Section
const ExperienceTitle = styled(motion.h2)`
  font-size: 3rem;
  font-weight: bold;
  margin-bottom: 2rem;
`;

// Styled component untuk pengalaman individual
const ExperienceItem = styled(motion.div)`
  margin-bottom: 1.5rem;
  padding: 1rem;
  border: 1px solid #ccc;
  border-radius: 8px;
  background-color: #fff; /* White background for items */
`;

// Styled component untuk nama posisi
const Position = styled.h3`
  font-size: 1.5rem;
  font-weight: bold;
`;

// Styled component untuk detail pengalaman
const Details = styled.p`
  font-size: 1rem;
  color: #555;
`;

const Experience = () => {
  // Add useEffect to initialize AOS
  useEffect(() => {
    AOS.init({
      duration: 1000,
      once: false,
    });
  }, []);

  return (
    <ExperienceSection
      id="experience"
      data-aos="fade-up" // AOS animation effect
    >
      <ExperienceContainer>
        <ExperienceTitle data-aos="fade-down">Pengalaman</ExperienceTitle>

        <ExperienceItem data-aos="fade-up">
          <Position>Desainer Grafis di Perusahaan ABC</Position>
          <Details>
            Januari 2020 - Sekarang: Menciptakan desain visual untuk berbagai proyek, termasuk logo, brosur, dan materi pemasaran.
          </Details>
        </ExperienceItem>

        <ExperienceItem data-aos="fade-up" data-aos-delay="100">
          <Position>UI/UX Designer di Perusahaan XYZ</Position>
          <Details>
            Maret 2018 - Desember 2019: Mengembangkan wireframes dan prototipe untuk aplikasi web dan seluler, berkolaborasi dengan tim pengembang untuk implementasi.
          </Details>
        </ExperienceItem>

        <ExperienceItem data-aos="fade-up" data-aos-delay="200">
          <Position>Programmer di Startup 123</Position>
          <Details>
            Mei 2017 - Februari 2018: Menulis kode untuk aplikasi web, berfokus pada pengembangan front-end menggunakan React.js dan JavaScript.
          </Details>
        </ExperienceItem>
      </ExperienceContainer>
    </ExperienceSection>
  );
};

export default Experience;
