import React, { useEffect } from 'react';
import styled from 'styled-components';
import Particles from 'react-tsparticles';
import AOS from 'aos'; 
import 'aos/dist/aos.css';

const HeroSection = styled.section`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100vh;
  position: relative;
  overflow: hidden;
`;

const HeroTitle = styled.h1`
  font-size: 5rem;
  font-family: 'Inter', sans-serif;
  font-weight: 1000;
  margin-bottom: 0rem;
  position: relative;
  z-index: 3;
`;

const HeroSubtitle = styled.p`
  font-size: 2rem;
  color: #555;
  z-index: 3;
`;

const Hero = () => {
  useEffect(() => {
    AOS.init({ duration: 1000, once: false });
  }, []);

  const particlesConfig = {
    particles: {
      number: {
        value: 50,
        density: {
          enable: true,
          value_area: 800,
        },
      },
      color: { value: "#ffffff" },
      shape: { type: "circle" },
      opacity: {
        value: 0.5,
        random: true,
      },
      size: {
        value: 5,
        random: true,
      },
      move: {
        enable: true,
        speed: 2,
        direction: "none",
        random: false,
        straight: false,
        outMode: "out",
      },
    },
  };

  return (
    <HeroSection>
      <Particles id="tsparticles" options={particlesConfig} />
      <HeroTitle data-aos="fade-up">Welcome To My Portfolio</HeroTitle>
      <HeroSubtitle data-aos="fade-up">Graphic Designer | UI/UX Designer | Programmer</HeroSubtitle>
    </HeroSection>
  );
};

export default Hero;
