import React, { useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import { motion } from 'framer-motion';
import AOS from 'aos'; 
import 'aos/dist/aos.css'; 

// Keyframes untuk animasi bergerak ke kanan dan ke kiri
const moveRightLeft = keyframes`
  0%, 100% {
    transform: translateX(-100%);
  }
  50% {
    transform: translateX(100%);
  }
`;

// Hero section dengan latar belakang dan teks
const HeroSection = styled.section`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background: #f5f5f5;
  color: #333;
  text-align: center;
  padding-top: 3rem;
  position: relative; /* Relative untuk positioning absolute animasi */
  overflow: hidden; /* Agar animasi tidak keluar dari batas */
`;

// Hero title dengan animasi outline bergerak
const HeroTitle = styled(motion.h1)`
  font-size: 4rem;
  font-weight: 1000;
  margin-bottom: 0rem;
  position: relative;
  z-index: 1; /* Pastikan teks utama berada di atas animasi */

  /* ::before untuk menciptakan outline bergerak */
  &::before {
    content: 'Welcome To My Portfolio'; /* Sama dengan teks utama */
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: -1; /* Di belakang teks utama */
    color: transparent;
    -webkit-text-stroke: 2px #007bff; /* Outline dengan warna biru */
    animation: ${moveRightLeft} 3s infinite linear; /* Animasi bergerak kanan-kiri */
    white-space: nowrap; /* Agar teks tidak membungkus */
  }
`;

// Subtitle tanpa animasi tambahan
const HeroSubtitle = styled(motion.p)`
  font-size: 1.5rem;
  color: #555;
`;

const Hero = () => {
  useEffect(() => {
    AOS.init({
      duration: 1000,
      once: false,
    });
  }, []);

  return (
    <HeroSection>
      <HeroTitle
        data-aos="fade-up"
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        Welcome To My Portfolio
      </HeroTitle>

      <HeroSubtitle
        data-aos="fade-up"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5, duration: 1 }}
      >
        Graphic Designer | UI/UX Designer | Programmer
      </HeroSubtitle>
    </HeroSection>
  );
};

export default Hero;
