// src/pages/About.js
import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const AboutSection = styled(motion.section)`
  padding: 4rem 2rem;
  background-color: #fff;
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
`;

const AboutTitle = styled(motion.h2)`
  font-size: 2.5rem;
  margin-bottom: 1rem;
`;

const AboutContent = styled(motion.p)`
  font-size: 1.2rem;
  max-width: 800px;
  color: #555;
`;

const About = () => {
  return (
    <AboutSection
      initial={{ opacity: 0, y: 50 }}
      whileInView={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.8 }}
      viewport={{ once: true }}
    >
      <AboutTitle
        initial={{ opacity: 0, x: -100 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 1 }}
      >
        Tentang Saya
      </AboutTitle>
      <AboutContent
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5, duration: 1 }}
      >
        Saya adalah seorang desainer grafis dengan pengalaman lebih dari 5 tahun dalam menciptakan desain yang menarik dan fungsional untuk berbagai merek.
      </AboutContent>
    </AboutSection>
  );
};

export default About;
