// src/components/Hero.js
import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const HeroSection = styled.section`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background: #f5f5f5;
  color: #333;
  text-align: center;
  padding-top: 4rem; /* Menghindari tertutup oleh Header */
`;

const HeroTitle = styled(motion.h1)`
  font Monserrat
  font-size: 4rem;
  font-weight: bold;
  margin-bottom: 1rem;
`;

const HeroSubtitle = styled(motion.p)`
  font-size: 1.5rem;
  color: #555;
`;

const Hero = () => (
  <HeroSection id="home"> 
    <HeroTitle
      initial={{ opacity: 0, y: -50 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 1 }}
    >
      WELLCOME TO MY PORTOFOLIO
    </HeroTitle>
    <HeroSubtitle
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ delay: 0.5, duration: 1 }}
    >
      Graphic Design | UI/UX Design | Programmer
    </HeroSubtitle>
  </HeroSection>
);

export default Hero;
